import React, { Component } from "react";

import axios from "axios";

const FATORIAL_REST_API_URL = "http://localhost:8080/api/fatorials";

const number = { number: this.state.number };

class FatorialComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number: 0,
    };
  }

  addFatorial() {
    axios.post(FATORIAL_REST_API_URL, { number });
  }

  render() {
    return (
      <div className="row">
        <div className="text-center">
          <form>
            <div className="form-group">
              <label>Calculate your fatorial here: </label>
              <input
                className="form-control"
                onChange={(event) =>
                  this.setState({ number: event.target.value })
                }
              ></input>
              <button className="btn btn-primary" onClick={this.addFatorial()}>
                calculate
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default FatorialComponent;
