import React, { Component } from "react";

import axios from "axios";

const FATORIAL_REST_API_URL = "http://localhost:8080/api/fatorials";

class FatorialComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fatorials: [],
    };
  }
  addFatorial() {
    axios.post(FATORIAL_REST_API_URL);
  }

  componentDidMount() {
    axios.get(FATORIAL_REST_API_URL).then((response) => {
      this.setState({ fatorials: response.data });
    });
  }

  render() {
    return (
      <div>
        <div className="container">
          <h1>Fatorial Calculator</h1>
          <div className="row">
            <h2 className="text-center">Calculate New Fatorial</h2>
            <form>
              <div className="form-group"></div>
            </form>
          </div>
          <div className="row">
            <h2 className="text-center">Fatorial List</h2>
            <table className="table table-striped">
              <thead>
                <tr>
                  <td>Number</td>
                  <td>Fatorial</td>
                </tr>
              </thead>
              <tbody>
                {this.state.fatorials.map((user) => (
                  <tr key={user.id}>
                    <td>user.number</td>
                    <td>user.fatorial</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default FatorialComponent;
