import React, { Component } from "react";

import axios from "axios";

const FATORIAL_REST_API_URL = "http://localhost:8080/api/fatorials";

class FatorialComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fatorials: [],
      number: 0,
    };
  }

  addFatorial() {
    axios.post(FATORIAL_REST_API_URL);
  }

  componentDidMount() {
    axios.get(FATORIAL_REST_API_URL).then((response) => {
      this.setState({ fatorials: response.data });
    });
  }

  render() {
    return (
      <div>
        <div className="container">
          <h1>Fatorial Calculator</h1>
          <div className="text-center">
            <div className="text-center">
              <form>
                <div className="form-group">
                  <label>Calculate your fatorial here: </label>
                  <input
                    className="form-control"
                    onChange={(event) =>
                      this.setState({ number: event.target.value })
                    }
                  ></input>
                  <button
                    className="btn btn-primary"
                    onClick={this.addFatorial()}
                  >
                    calculate
                  </button>
                </div>
              </form>
            </div>
          </div>
          <div className="row">
            <h2 className="text-center">Fatorial List</h2>
            <table className="table table-striped">
              <thead>
                <tr>
                  <td>Number</td>
                  <td>Fatorial</td>
                </tr>
              </thead>
              <tbody>
                {this.state.fatorials.map((user) => (
                  <tr key={user.id}>
                    <td>user.number</td>
                    <td>user.fatorial</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default FatorialComponent;
