import React, { Component } from "react";

import axios from "axios";

const FATORIAL_REST_API_URL = "http://localhost:8080/api/fatorials";

class FatorialComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number: "",
      fatorial: "",
    };
  }

  addFatorial() {
    axios.post(FATORIAL_REST_API_URL);
  }

  render() {
    return (
      <div className="row">
        <div className="text-center">
          <form>
            <div className="form-group">
              <label>Calculate your fatorial here: </label>
              <input
                className="form-control"
                onChange={(event) =>
                  this.setState([
                    { number: event.target.value },
                    { fatorial: event.target.value },
                  ])
                }
              ></input>
              <button
                type="submit"
                className="btn btn-primary"
                onClick={this.addFatorial()}
              >
                calculate
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default FatorialComponent;
