import React, { Component } from "react";

import axios from "axios";

const FATORIAL_REST_API_URL = "http://localhost:8080/api/fatorials";
const number = 0;
class FatorialComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number: 0,
    };
  }

  handleChange = (event) => {
    this.setState({ number: event.target.value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const send = {
      number: this.state.number,
    };
  };

  addFatorial() {
    axios.post(FATORIAL_REST_API_URL);
  }

  render() {
    return (
      <div className="row">
        <div className="text-center">
          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Calculate your fatorial here: </label>
              <input
                className="form-control"
                onChange={(event) => this.setState(this.handleChange())}
              ></input>
              <button className="btn btn-primary" onClick={this.addFatorial()}>
                calculate
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default FatorialComponent;
